package com.dylan.rbac.server.controller.auth;import com.dylan.rbac.api.auth.RoleService;import com.dylan.rbac.common.Result;import com.dylan.rbac.common.controller.BaseController;import com.dylan.rbac.common.util.ResultUtil;import com.dylan.rbac.data.model.auth.Role;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.servlet.http.HttpServletRequest;@RestController@RequestMapping("/auth")public class RoleController extends BaseController {    @Autowired    private RoleService roleService;    @GetMapping("/role/{id}")    public Result<Role> getRoleById(@PathVariable Long id, HttpServletRequest request) {        return ResultUtil.invoke(this, request, () -> roleService.get(id));    }}